import React from "react";

// In case you dont want to use styled component move it to stylesheets
// and reference it from there
import styled, { keyframes } from "styled-components";

/**
 * Is the parent container that encapsulates loaders
 */
const loaderContainer = {
  padding: "10px"
};

/**
 * Animation keyframes
 */
// Create the keyframes
const rotate = keyframes`
  from {
    transform: rotate(0deg);
  }
  to {
    transform: rotate(360deg);
  }
`;
// Here we create a component that will rotate everything we pass in over two seconds
const Rotate = styled.div`
  display: inline-block;
  animation: ${rotate} 2s linear infinite;
  padding: 2rem 1rem;
  font-size: 1.2rem;
`;

const keyFrames = keyframes`
  0%{
      background-position: -400px 0
  }
  100%{
      background-position: 400px 0
  }
`;

/**
 * Actual animation of loader to get the transition effect
 */
const loaderAnimation = {
  animationDuration: "4s",
  animationFillMode: "forwards",
  animationIterationCount: "infinite",
  animationTimingFunction: "linear",
  background: "linear-gradient(110deg, #eeeeee 8%, #dddddd 18%, #eeeeee 33%)",
  position: "relative",
  backgroundSize: `400px 400px`
};

/**
 * Re-usable style components withing loader elements
 */
const loaderConstants = `
  backgroundColor: "#f6f7f8",
  borderRadius: "4px",
  marginTop: "5px"
  animation: ${rotate} 2s linear infinite;
`;

/**
 * Stub for images of given width and height
 */
const loaderImage = {
  ...loaderConstants,
  width: "200px",
  height: "120px"
};

/**
 * Stub for small texts
 */
const loaderSmall = {
  ...loaderConstants,
  width: "100px",
  height: "12px"
};

/**
 * Stub for large texts
 */
const loaderLarge = {
  ...loaderConstants,
  width: "160px",
  height: "12px"
};

/**
 * Stub for images
 */
export const LoaderImage = props => <div style={props.style}></div>;

/**
 * Stub for small text
 */
export const LoaderTextSmall = props => <div style={props.style}></div>;

/**
 * Stub for small text
 */
export const LoaderTextLarge = props => <div style={props.style}></div>;

/**
 * Loader compoent
 * @return {Loader} Loader with Image and variable length texts
 */
export const Loader = () => (
  <div style={loaderContainer}>
    <Rotate>
      <LoaderImage style={loaderImage} />{" "}
    </Rotate>

    <LoaderTextSmall style={loaderSmall} />
    <LoaderTextLarge style={loaderLarge} />
    <LoaderTextLarge style={loaderLarge} />
  </div>
);
